import React from 'react';
import { Image, StyleSheet, Text, TouchableOpacity, View } from 'react-native';

export default function Tile(props) {
  return (
    <View style={styles.container}>
      <Image source={{uri: `data:image/gif;base64,${props.base64data}`}}  style = {styles.tile} /> 
       
      <TouchableOpacity onPress = {() => alert('user liked pic')}
      style = {styles.button}>
        <Text style= {styles.buttonText}>Like</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  tile:{
    width: 305, 
    height: 305,
    marginBottom: 0,
  },
  instructions:{
    color: '#888', 
    fontSize: 18,
    marginHorizontal: 15,
  },
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    backgroundColor: "blue",
    padding: 20,
    borderRadius: 5,
  },
  buttonText: {
    fontSize: 20,
    color: '#fff',
  }
});
