import React from 'react';
import { Image, StyleSheet, Text, TouchableOpacity, View, ScrollView } from 'react-native';
import Tile from './Tile';
/* props:
      cards
      cardNames
      cardHandler
      */


export default class ImageGallery extends React.Component {

  constructor(props){
    super(props);
  }

  changeSubmission(idx){
    const cardName = props.cardNames[idx];
    props.cardHandler([cardName,idx]);
  }

  render() {
    return (
      <View style={styles.container}>
          <ScrollView style= {styles.gallery}>
               <Tile base64data = {this.props.cards[0]} cardName = {this.props.cardNames[0]} onLikeHandler = {this.changeSubmission(1).bind(this,0)}></Tile>
               <Tile base64data = {this.props.cards[1]} cardName = {this.props.cardNames[1]} onLikeHandler = {this.changeSubmission(1).bind(this,1)}></Tile>
               <Tile base64data = {this.props.cards[2]} cardName = {this.props.cardNames[2]} onLikeHandler = {this.changeSubmission(2).bind(this,2)}></Tile>
               <Tile base64data = {this.props.cards[3]} cardName = {this.props.cardNames[3]} onLikeHandler = {this.changeSubmission(3).bind(this,3)}></Tile>
               <Tile base64data = {this.props.cards[4]} cardName = {this.props.cardNames[4]} onLikeHandler = {this.changeSubmission(4).bind(this,4)}></Tile>
               <Tile base64data = {this.props.cards[5]} cardName = {this.props.cardNames[5]} onLikeHandler = {this.changeSubmission(5).bind(this,5)}></Tile>
               <Tile base64data = {this.props.cards[6]} cardName = {this.props.cardNames[6]} onLikeHandler = {this.changeSubmission(6).bind(this,6)}></Tile>
               <Tile base64data = {this.props.cards[7]} cardName = {this.props.cardNames[7]} onLikeHandler = {this.changeSubmission(7).bind(this,7)}></Tile> 
          </ScrollView>
      </View>
    )
  };
}

const styles = StyleSheet.create({
  instructions:{
    color: '#888', 
    fontSize: 18,
    marginHorizontal: 15,
  },
  gallery:{
    height: 300,
  },
  container: {
    flex: 1,
    height: 300,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    backgroundColor: "blue",
    padding: 20,
    borderRadius: 5,
  },
  buttonText: {
    fontSize: 20,
    color: '#fff',
  }
});
