import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Modal, StyleSheet, TextInput, Button, View} from 'react-native';
  

export default function IntroModal(props) {
  const [text, onChangeText] = React.useState("");
  
  function handleNickname(nKname) {
    props.changeNickname(nKname);
  }

  return (
    <View style={styles.container}>
      Please enter your nickname:
      <TextInput
      onChangeText={onChangeText}
      value = {text}
      />
      <Button
      onClick= {handleNickname(text)}
      ></Button>
    </View>
  );
}

const styles = StyleSheet.create({
 
  instructions:{
    color: '#888', 
    fontSize: 18,
    marginHorizontal: 15,
  },
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    backgroundColor: "blue",
    padding: 20,
    borderRadius: 5,
  },
  buttonText: {
    fontSize: 20,
    color: '#fff',
  }
});
