import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Modal, StyleSheet, TextInput, Text, TouchableOpacity } from 'react-native';
  

export default function IntroModal(props) {
  const [text, onChangeText] = React.useState("");
  const [gameRoomNum, setGameRoomNum] = React.useState("");

  function submitModal() {
    props.changeNickname(this.state.text);
    props.joinGame(gameRoomNum);
  }

  return (
    <Modal style={styles.container}>
      
      <TextInput
        style = {styles.inputLine}
        onChangeText={onChangeText}
        placeholder={"nickname"}
      />
      <Text>If you have a game code, enter it here:</Text>
      <Text>Otherwise, leave it blank</Text>
      <TextInput
        style = {styles.inputLine}
        onChangeText={setGameRoomNum}
      />

      <TouchableOpacity 
       style = {styles.button}
       onPress= {submitModal}>
        <Text style= {styles.buttonText}>Join</Text>
      </TouchableOpacity>

    </Modal>
  );
}

const styles = StyleSheet.create({
 
  instructions:{
    color: '#888', 
    fontSize: 18,
    marginHorizontal: 15,
  },
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    backgroundColor: "blue",
    padding: 20,
    borderRadius: 5,
  },
  inputLine: {
    backgroundColor: "grey",
    padding: 20,
    borderRadius: 5,
  },
  buttonText: {
    fontSize: 20,
    color: '#fff',
  }
});
