import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Modal, StyleSheet, TextInput, Text, TouchableOpacity } from 'react-native';
  

export default function SubmissionsModal(props) {
    var love, hate;
  function submitModal() {
    props.submitRatings([love,hate]);
  }

  function processLike(idx){
    love = idx;
  }

  function processDislike(idx){
    hate = idx;
  }

  return (
    <Modal style={styles.container}>
      
      <Text>Pick the best and the worst:</Text>

      <ScrollView style= {styles.gallery}>
        const cardList = props.cards.map((item, j) => {
           <Tile base64data = {item[j]} 
           onLikeHandler = {this.processLike} 
           onDisLikeHandler = {this.processDislike}
           cardName = {this.props.cardNameArray[j]}
           ></Tile>
    });
        </ScrollView>

      <TouchableOpacity 
       style = {styles.button}
       onPress= {submitModal}>
        <Text style= {styles.buttonText}>Done</Text>
      </TouchableOpacity>

    </Modal>
  );
}

const styles = StyleSheet.create({
 
  instructions:{
    color: '#888', 
    fontSize: 18,
    marginHorizontal: 15,
  },
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    backgroundColor: "blue",
    padding: 20,
    borderRadius: 5,
  },
  inputLine: {
    backgroundColor: "grey",
    padding: 20,
    borderRadius: 5,
  },
  buttonText: {
    fontSize: 20,
    color: '#fff',
  }
});
