import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Image, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import logo from './assets/logo.gif';
import socketIOClient from "socket.io-client";
import ImageGallery from './components/ImageGallery.js';
import Header from './components/Header.js';
import Footer from './components/Footer.js';
import IntroModal from './components/IntroModal.js';
import SubmissionsModal from './components/SubmissionsModal';
import WinnerModal from './components/WinnerModal.js';

export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      message: "dickhead",
      score : 1,
      message : "fuck you",
      nickname: "milo",
      roomNum: "",
      submissionCardDetails : [ "",0],
      submissionsToRate : [],
      submissionsToRateDetails : [],
      cardNameArray : [0,0,0,0,0,0,0,0],
      cardArray : [
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      ],
      prompt: "",
      winner: [],
      modalType: "intro",
      mainButtonText: "Start Game"
    };
  }

    socket = socketIOClient("http://localhost:9000");

    handleNickname = (nickN) => {
      this.setState({nickname: nickN});
      this.socket.emit("nickname", nickN);
    }

    componentDidMount() {
      this.configureSocket();
    }
    configureSocket() {
      this.socket.on("message", (msg) => {
        this.setState({message : msg});
      });

      this.socket.on("card", ([cardName, data]) => {
        const subCardIdx = this.state.submissionCardDetails[1];
        this.setState({cardArray : this.onNewCard(subCardIdx, data, this.state.cardArray)});
        this.setState({cardNameArray : this.onNewCard(subCardIdx, cardName, this.state.cardNameArray)});
        this.setState({mainButtonText: "Choose Card"});
        if(this.state.cards.length !== 7){
          this.setState({submissionCardDetails:["", (subCardIdx+1)]})
        }  
      });

      this.socket.on("prompt" , s => {
        this.setState({prompt: s});
      });

      this.socket.on("modal", a => {
        var cardIndexList = [];
        var cardDataList = [];
        a.forEach((card, j) => {
          cardIndexList[j] = a[j][0];
          cardDataList[j] = a[j][1];
        })
        this.setState({
          submissionsToRate : cardDataList,
          submissionsToRateDetails : cardIndexList,
        });
        this.setState({
          modalType: "submission",
        });
        setTimeout(() => {
          this.setState({modalType: "none",});
        }, 10000);
      });

      this.socket.on("winner", a => {
        this.setState( {winner: a});
        this.setState( {modal: winner});
        this.setState({modalType: "winner",})
      });
    }

    onNewCard = (i, data, arrayToBeIterated) => {
      this.setState(state => {
        const list = arrayToBeIterated.map((item, j) => {
          if (j === i) {
            return data;
          } else {
            return item;
          }
        });
        return {
          list,
        };
      });
    };

    joinCorrectTypeGameRoom = idx => {
      if(idx.length === 0){
        this.socket.emit("startRoom",idx);
      } else {
        this.socket.emit("joinRoom",idx);
        this.setState({roomNum : idx});
      }
      this.setState({modalType: "none" })
    }

    submitRatings(tuple){
      this.socket.emit("vote", tuple);
    }

    changingMainButtonHandler = () => {
      if (this.state.mainButtonText === "Choose Card"){ (idx) => {
          var subIndex = this.state.cardNameArray[idx];
          this.setState({ submissionCardDetails : [subIndex,idx],});
          this.socket.emit("submission", subIndex )
        }
      } else {
        this.socket.emit("startGame","");
        console.log("startedGame");
      }
    };

    render(){
      var modalVariable;
      if (this.state.modalType === "intro"){
        modalVariable = <IntroModal changeNickname = {this.handleNickname.bind(this)} joinGame = {this.joinCorrectTypeGameRoom.bind(this)}/>
      } else if (this.state.modalType === "submission"){
        modalVariable = <SubmissionsModal cards = {this.state.submissionsToRate} cardNameArray = {this.submissionsToRateDetails} submitRatings = {this.submitRatings.bind(this)}/>
      } else if (this.state.modalType === "winner"){
        modalVariable = <WinnerModal winner= {this.state.winner}/>
      }
      else if (this.state.modalType === "none"){
        modalVariable = null;
      }



      return (
            <View style={styles.container}>
              {modalVariable}
              <Header score = {this.state.score} nickname = {this.state.nickname} gameRoomName = {this.state.roomNum}/>
              <ImageGallery 
              cards = {this.state.cardArray} 
              cardHandler = {this.processOutgoingSubmission} 
              />
              <Footer msg = {this.state.message} 
              clickHandler = {this.changingMainButtonHandler.bind(this)} 
              buttonText = {this.state.mainButtonText}
              prompt = {this.state.prompt}/>
            </View>
          );   
    }


}

const styles = StyleSheet.create({

  instructions:{
    color: '#888', 
    fontSize: 18,
    marginHorizontal: 15,
  },
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    backgroundColor: "blue",
    padding: 20,
    borderRadius: 5,
  },
  buttonText: {
    fontSize: 20,
    color: '#fff',
  }
});
