import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Image, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import logo from './assets/logo.gif';
import socketIOClient from "socket.io-client";
import ImageGallery from './components/ImageGallery.js';
import Header from './components/Header.js';
import Footer from './components/Footer.js';
import IntroModal from './components/IntroModal.js';

export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      message: "dickhead",
      score : 1,
      message : "fuck you",
      nickname : "milo",
      roomNum: "",
      submissionCardDetails : [ "",0],
      cardNameArray : [0,0,0,0,0,0,0,0],
      cardArray : [
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      ]
    };
  }

    socket = socketIOClient("http://localhost:9000");
    startGameClickHandler = () => {
      console.log("button Clicked");
    }
    handleNickname = nickN => {
      this.setState({nickname : nickN});
      this.socket.emit("nickname", this.state.nickname);
    }

    componentDidMount() {
      this.configureSocket();
    }
    configureSocket() {
      this.socket.on("message", (msg) => {
        this.setState({message : msg});
      });

      this.socket.on("card", ([cardName, data]) => {
        const subCardIdx = this.state.submissionCardDetails[1];
        this.setState({cards : this.onNewCard(this.state.submissionCardDetails[1], data)});
          this.setState({cardNameArray : this.onNewCard(this.state.submissionCardDetails[1], cardName)});
        if(this.state.cards.length !== 7){
          this.setState({submissionCardDetails:["", (submissionCardDetails[1]+1)]})
        }  
      })
    }

    onNewCard = (i, data) => {
      this.setState(state => {
        const list = state.cards.map((item, j) => {
          if (j === i) {
            return data;
          } else {
            return item;
          }
        });

        return {
          list,
        };
      });
    };

    joinCorrectTypeGameRoom = idx => {
      if(idx.length === 0){
        this.socket.emit("startRoom",idx);
      } else {
        this.socket.emit("joinRoom",idx);
        this.setState({roomNum : idx});
      }
    }

    render(){
        return (
            <View style={styles.container}>
              <IntroModal 
              changeNickname = {this.handleNickname.bind(this)}
              joinGame = {this.joinCorrectTypeGameRoom.bind(this)}
              />
              <Header score = {this.state.score} nickname = {this.state.nickname} />
              <ImageGallery cards = {this.state.cardArray} />
              <Footer msg = {this.state.message} clickHandler = {this.startGameClickHandler}/>
            </View>
          );   
    }


}

const styles = StyleSheet.create({

  instructions:{
    color: '#888', 
    fontSize: 18,
    marginHorizontal: 15,
  },
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    backgroundColor: "blue",
    padding: 20,
    borderRadius: 5,
  },
  buttonText: {
    fontSize: 20,
    color: '#fff',
  }
});
