import React from 'react';
import { Image, StyleSheet, Text, TouchableOpacity, View, ScrollView } from 'react-native';
import Tile from './Tile';
/* props:
      cards
      cardNames
      cardHandler
      */


export default function ImageGallery(props) {

  function changeSubmission(idx){
    const cardName = props.cardNames[idx]
    props.cardHandler(cardName,idx);
  }


  return (
    <View style={styles.container}>
        <ScrollView style= {styles.gallery}>
             <Tile base64data = {props.cards[0]} cardName = {props.cardNames[0]} onLikeHandler = {changeSubmission(0)}></Tile>
             <Tile base64data = {props.cards[1]} cardName = {props.cardNames[1]} onLikeHandler = {changeSubmission(1)}></Tile>
             <Tile base64data = {props.cards[2]} cardName = {props.cardNames[2]} onLikeHandler = {changeSubmission(2)}></Tile>
             <Tile base64data = {props.cards[3]} cardName = {props.cardNames[3]} onLikeHandler = {changeSubmission(3)}></Tile>
             <Tile base64data = {props.cards[4]} cardName = {props.cardNames[4]} onLikeHandler = {changeSubmission(4)}></Tile>
             <Tile base64data = {props.cards[5]} cardName = {props.cardNames[5]} onLikeHandler = {changeSubmission(5)}></Tile>
             <Tile base64data = {props.cards[6]} cardName = {props.cardNames[6]} onLikeHandler = {changeSubmission(6)}></Tile>
             <Tile base64data = {props.cards[7]} cardName = {props.cardNames[7]} onLikeHandler = {changeSubmission(7)}></Tile> 
        </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  instructions:{
    color: '#888', 
    fontSize: 18,
    marginHorizontal: 15,
  },
  gallery:{
    height: 300,
  },
  container: {
    flex: 1,
    height: 300,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    backgroundColor: "blue",
    padding: 20,
    borderRadius: 5,
  },
  buttonText: {
    fontSize: 20,
    color: '#fff',
  }
});
