import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Modal, StyleSheet, TextInput, Button } from 'react-native';
  

export default function IntroModal(props) {
  const [text, onChangeText] = React.useState("Nickname");
  
  return (
    <Modal>
      Please enter your nickname:
      <TextInput
      style={styles.input}
      onChangeText={onChangeText}
      value={text}
      />
      <Button
      onClick={this.props.changeNickname(text)}
      ></Button>
    </Modal>
  );
}

const styles = StyleSheet.create({
 
  instructions:{
    color: '#888', 
    fontSize: 18,
    marginHorizontal: 15,
  },
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    backgroundColor: "blue",
    padding: 20,
    borderRadius: 5,
  },
  buttonText: {
    fontSize: 20,
    color: '#fff',
  }
});
